import React from 'react';
import { formatTime } from '../utils/formatTime';

const GameOverScreen = ({ elapsedTime, flipCount, bestTime }) => (
  <div className="game-over popup">
    <img src="/you-win.png" alt="You Win!" className="you-win-image" />
    <p>Time to complete: <strong className="yellow-text">{formatTime(elapsedTime)}</strong></p>
    <p>Number of flips: <strong className="yellow-text">{flipCount}</strong></p>
    <p>Best time: <strong className="yellow-text">{bestTime !== null ? formatTime(bestTime) : "N/A"}</strong></p>
  </div>
);

export default GameOverScreen;
