// components/SearchBar.js
// components/SearchBar.js
import React, { useState, useEffect } from 'react';
import './App.css'; // Make sure this line exists if you're using a separate CSS file

const SearchBar = ({
  query,
  setQuery,
  fetchImages,
  loading,
  imageCount,
  setImageCount,
  handleUploadImages
}) => {
  const [mode, setMode] = useState('topic'); // 'topic' or 'upload'
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setUploadedFiles(files);
    setError('');
  };

  const handleGenerateGame = () => {
    if (mode === 'topic') {
      fetchImages(imageCount);
    } else {
      if (uploadedFiles.length < imageCount) {
        setError(`Please upload at least ${imageCount} image(s).`);
        return;
      }
      handleUploadImages(uploadedFiles.slice(0, imageCount));
    }
  };

  useEffect(() => {
    if (mode === 'upload' && uploadedFiles.length >= imageCount) {
      setError('');
    }
  }, [uploadedFiles, imageCount, mode]);

  return (
    <div className="search-bar">
      <div className="pair-count-row">
        <label htmlFor="pairCount" className="pair-count-label">
          Number of Pairs:
        </label>
        <input
          id="pairCount"
          type="number"
          min="2"
          max="15"
          value={imageCount}
          onChange={(e) => {
            const value = Math.max(2, Number(e.target.value));
            setImageCount(value);
          }}
          className="pair-count-input"
        />
      </div>

      <div className="mode-select-row">
        <label htmlFor="modeSelect" className="mode-select-label">
          Choose Mode:
        </label>
        <select
          id="modeSelect"
          value={mode}
          onChange={(e) => {
            setMode(e.target.value);
            setUploadedFiles([]);
            setError('');
          }}
          className="mode-select"
        >
          <option value="topic">Search by Topic</option>
          <option value="upload">Upload Your Images</option>
        </select>
      </div>

      {mode === 'topic' ? (
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Type a topic"
          className="topic-input"
        />
      ) : (
        <div className="upload-section">
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={handleFileChange}
            className="upload-input"
          />
          <p className="upload-count">{uploadedFiles.length} image(s) selected</p>
        </div>
      )}

      {error && <p className="error-message">{error}</p>}

      <button
        onClick={handleGenerateGame}
        disabled={loading || (mode === 'topic' && !query)}
        className="generate-button"
      >
        {loading ? 'Loading...' : 'Generate Game'}
      </button>
    </div>
  );
};

export default SearchBar;
