import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [cards, setCards] = useState([]);
  const [flipped, setFlipped] = useState([]);
  const [matched, setMatched] = useState([]);
  const [disabled, setDisabled] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [flipCount, setFlipCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);

  const UNSPLASH_ACCESS_KEY = 'eMQx1uzxyUWvXl2l8Gj9DlKWe6SNN5KsC8cQ2srcvkc'; // ‚¨ÖÔ∏è Replace with your key

  useEffect(() => {
    let interval;
    if (startTime) {
      interval = setInterval(() => {
        setElapsedTime(Math.floor((Date.now() - startTime) / 1000));
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [startTime]);

  useEffect(() => {
    if (matched.length === cards.length / 2 && cards.length > 0) {
      setStartTime(null);
    }
  }, [matched, cards]);

  const fetchImages = async () => {
    if (!query) return;
    setLoading(true);
    try {
      const res = await fetch(
        `https://api.unsplash.com/search/photos?query=${query}&per_page=10&client_id=${UNSPLASH_ACCESS_KEY}`
      );
      const data = await res.json();
      const images = data.results.map((img) => ({
        id: uuidv4(),
        src: img.urls.small,
      }));

      const duplicated = images.flatMap((img) => [
        { ...img, uniqueId: uuidv4() },
        { ...img, uniqueId: uuidv4() },
      ]);
      const shuffled = duplicated.sort(() => Math.random() - 0.5);
      setCards(shuffled);
      setFlipped([]);
      setMatched([]);
      setElapsedTime(0);
      setFlipCount(0);
      setStartTime(Date.now());
      setGameStarted(true);
    } catch (err) {
      console.error('Error fetching images:', err);
    } finally {
      setLoading(false);
    }
  };

  const resetGameWithSameImages = () => {
    const uniqueImages = cards
      .filter((card, index, self) =>
        index === self.findIndex((c) => c.src === card.src)
      );

    const duplicated = uniqueImages.flatMap((img) => [
      { ...img, uniqueId: uuidv4() },
      { ...img, uniqueId: uuidv4() },
    ]);
    const shuffled = duplicated.sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setFlipped([]);
    setMatched([]);
    setElapsedTime(0);
    setFlipCount(0);
    setStartTime(Date.now());
  };

  const handleClick = (card) => {
    if (disabled || flipped.find((f) => f.uniqueId === card.uniqueId) || matched.includes(card.src)) return;

    const newFlipped = [...flipped, card];
    setFlipped(newFlipped);

    if (newFlipped.length === 2) {
      setDisabled(true);
      setFlipCount((prev) => prev + 1);

      if (newFlipped[0].src === newFlipped[1].src) {
        setMatched([...matched, card.src]);
        setTimeout(() => {
          setFlipped([]);
          setDisabled(false);
        }, 700);
      } else {
        setTimeout(() => {
          setFlipped([]);
          setDisabled(false);
        }, 1000);
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60).toString().padStart(2, '0');
    const secs = (seconds % 60).toString().padStart(2, '0');
    return `${mins}:${secs}`;
  };

  return (
    <div className="app">
      <h1>Memory Game Generator</h1>

      {!gameStarted ? (
        <div className="search-bar">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type a topic"
          />
          <button onClick={fetchImages} disabled={loading || !query}>
            {loading ? 'Loading...' : 'Generate Game'}
          </button>
        </div>
      ) : (
        <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '1rem', flexWrap: 'wrap' }}>
          <button onClick={() => {
            setCards([]);
            setQuery('');
            setGameStarted(false);
            setMatched([]);
            setFlipped([]);
            setElapsedTime(0);
            setFlipCount(0);
            setStartTime(null);
          }} className="new-game-button">
            Generate New Game
          </button>
          <button onClick={resetGameWithSameImages} className="play-again-button">
            üîÅ Play Again with Same Images
          </button>
        </div>
      )}

      {cards.length > 0 && (
        <div className="stats-bar">
          <div className="stat-item"><span className="stat-emoji">üèÖ</span> Score: {matched.length}</div>
          <div className="stat-item"><span className="stat-emoji">üîÑ</span> Flips: {flipCount}</div>
          <div className="stat-item"><span className="stat-emoji">‚è±Ô∏è</span> Timer: {formatTime(elapsedTime)}</div>
        </div>
      )}

      <div className="grid">
        {cards.map((card) => {
          const isFlipped = flipped.find((f) => f.uniqueId === card.uniqueId) || matched.includes(card.src);
          return (
            <div key={card.uniqueId} className="card" onClick={() => handleClick(card)}>
              <div className={`card-inner ${isFlipped ? 'flipped' : ''}`}>
                <div className="card-front">
                  <img src="/card-back.png" alt="Back" />
                </div>
                <div className="card-back">
                  <img src={card.src} alt="Card" />
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {matched.length === cards.length / 2 && cards.length > 0 && (
        <div className="win-message">
          üéâ You matched all pairs in {formatTime(elapsedTime)} with {flipCount} flips!
        </div>
      )}
    </div>
  );
}

export default App;
