import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [cards, setCards] = useState([]);
  const [flipped, setFlipped] = useState([]);
  const [matched, setMatched] = useState([]);
  const [disabled, setDisabled] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0);

  // ‚è≥ Timer logic
  useEffect(() => {
    let interval;

    if (startTime) {
      interval = setInterval(() => {
        setElapsedTime(Math.floor((Date.now() - startTime) / 1000));
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [startTime]);

  useEffect(() => {
    if (matched.length === files.length && files.length > 0) {
      // Game won
      setStartTime(null); // stop timer
    }
  }, [matched, files]);

  const handleFiles = (e) => {
    const uploaded = Array.from(e.target.files).map(file => ({
      id: uuidv4(),
      src: URL.createObjectURL(file)
    }));
    setFiles(uploaded);
  };

  const startGame = () => {
    const duplicated = files.flatMap(file => [
      { ...file, uniqueId: uuidv4() },
      { ...file, uniqueId: uuidv4() }
    ]);
    const shuffled = duplicated.sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setFlipped([]);
    setMatched([]);
    setElapsedTime(0);
    setStartTime(Date.now()); // starts the timer
  };

  const handleClick = (card) => {
    if (disabled || flipped.find(f => f.uniqueId === card.uniqueId) || matched.includes(card.src)) return;

    const newFlipped = [...flipped, card];
    setFlipped(newFlipped);

    if (newFlipped.length === 2) {
      setDisabled(true);
      if (newFlipped[0].src === newFlipped[1].src) {
        setMatched([...matched, card.src]);
        setTimeout(() => {
          setFlipped([]);
          setDisabled(false);
        }, 700);
      } else {
        setTimeout(() => {
          setFlipped([]);
          setDisabled(false);
        }, 1000);
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60).toString().padStart(2, '0');
    const secs = (seconds % 60).toString().padStart(2, '0');
    return `${mins}:${secs}`;
  };

  return (
    <div className="app">
      <h1>üß† Memory Game with Your Photos</h1>

      <input type="file" accept="image/*" multiple onChange={handleFiles} />
      <button onClick={startGame} disabled={files.length === 0}>Start Game</button>

      <div className="timer">‚è±Ô∏è Time: {formatTime(elapsedTime)}</div>



      <div className="grid">
        {cards.map(card => {
          const isFlipped = flipped.find(f => f.uniqueId === card.uniqueId) || matched.includes(card.src);
          return (
            <div key={card.uniqueId} className="card" onClick={() => handleClick(card)}>
              <div className={`card-inner ${isFlipped ? 'flipped' : ''}`}>
                <div className="card-front">
                  <img src="/card-back.png" alt="Back" />
                </div>
                <div className="card-back">
                  <img src={card.src} alt="Card" />
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {matched.length === files.length && files.length > 0 && (
        <div className="win-message">
          üéâ You matched all pairs in {formatTime(elapsedTime)}!
        </div>
      )}
    </div>
  );
}

export default App;
