// components/SearchBar.js
import React, { useState, useEffect } from 'react';

const SearchBar = ({
  query,
  setQuery,
  fetchImages,
  loading,
  imageCount,
  setImageCount,
  handleUploadImages
}) => {
  const [mode, setMode] = useState('topic'); // 'topic' or 'upload'
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setUploadedFiles(files);
    setError('');
  };

  const handleGenerateGame = () => {
    if (mode === 'topic') {
      fetchImages(imageCount);
    } else {
      if (uploadedFiles.length < imageCount) {
        setError(`Please upload at least ${imageCount} images.`);
        return;
      }
      handleUploadImages(uploadedFiles.slice(0, imageCount));
    }
  };

  // Clear error if files change and are valid
  useEffect(() => {
    if (mode === 'upload' && uploadedFiles.length >= imageCount) {
      setError('');
    }
  }, [uploadedFiles, imageCount, mode]);

  return (
    <div className="search-bar">
      <div>
        <label>Number of Pairs:</label>
        <input
          type="number"
          min="2"
          max="15"
          value={imageCount}
          onChange={(e) => setImageCount(Number(e.target.value))}
        />
      </div>

      <div>
        <label>Choose Mode:</label>
        <select value={mode} onChange={(e) => {
          setMode(e.target.value);
          setUploadedFiles([]);
          setError('');
        }}>
          <option value="topic">Search by Topic</option>
          <option value="upload">Upload Your Images</option>
        </select>
      </div>

      {mode === 'topic' ? (
        <>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type a topic"
          />
        </>
      ) : (
        <>
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={handleFileChange}
          />
          <p>{uploadedFiles.length} image(s) selected</p>
        </>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <button onClick={handleGenerateGame} disabled={loading || (mode === 'topic' && !query)}>
        {loading ? 'Loading...' : 'Generate Game'}
      </button>
    </div>
  );
};

export default SearchBar;
