import React from 'react';
import { formatTime } from '../utils/formatTime';

const GameOverScreen = ({ elapsedTime, flipCount, bestTime }) => (
  <div className="game-over popup">
    <img src="/you-win.png" alt="You Win!" className="you-win-image" />
    <p className="yellow-text">Time to complete: <strong>{formatTime(elapsedTime)}</strong></p>
    <p className="yellow-text">Number of flips: <strong>{flipCount}</strong></p>
    <p className="yellow-text">Best time: <strong className="yellow-text">{bestTime !== null ? formatTime(bestTime) : "N/A"}</strong></p>
  </div>
);

export default GameOverScreen;
