// SearchBar.js
import React, { useState } from 'react';

const SearchBar = ({ query, setQuery, fetchImages, loading, setImageCount, imageCount, handleUploadImages }) => {
  const [mode, setMode] = useState('topic'); // 'topic' or 'upload'
  const [uploadedFiles, setUploadedFiles] = useState([]);

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files).slice(0, imageCount);
    setUploadedFiles(files);
    handleUploadImages(files); // pass to parent
  };

  return (
    <div className="search-bar">
      <div>
        <label>Number of Pairs:</label>
        <input
          type="number"
          min="2"
          max="15"
          value={imageCount}
          onChange={(e) => setImageCount(Number(e.target.value))}
        />
      </div>

      <div>
        <label>Choose Mode:</label>
        <select value={mode} onChange={(e) => setMode(e.target.value)}>
          <option value="topic">Search by Topic</option>
          <option value="upload">Upload Your Images</option>
        </select>
      </div>

      {mode === 'topic' ? (
        <>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type a topic"
          />
          <button onClick={() => fetchImages(imageCount)} disabled={loading || !query}>
            {loading ? 'Loading...' : 'Generate Game'}
          </button>
        </>
      ) : (
        <>
          <input type="file" accept="image/*" multiple onChange={handleFileChange} />
        </>
      )}
    </div>
  );
};

export default SearchBar;
